<?phprequire_once "library.inc.php";$csv_current_row = "";function csv_start ($filename){	header ("Content-Type: text/x-csv");	header ("Expires: " . gmdate("D, d M Y H:i:s") . " GMT");	header ("Content-Disposition: inline; filename=\"" . $filename . ".csv\"");	header ("Cache-Control: must-revalidate, post-check=0, pre-check=0");	header ("Pragma: no-cache");}function csv ($value){	global $csv_current_row;	$csv_current_row = dbAddToList ($csv_current_row, "\"$value\"", ",");}function csv_newline (){	global $csv_current_row;	echo "$csv_current_row\r";	$csv_current_row = "";}function csv_end (){	csv_newline ();}?>