SELECT c.primary_name, c.first_name, max( d.amount )  AS max_fy_2002_gift, max( d2.amount )  AS max_fy_2003_giftFROM contacts AS cJOIN donation_associations AS da ON da.contact_id = c.contact_idJOIN donations AS d ON d.donation_id = da.donation_id AND d.date >=  '2002-10-1' AND d.date <=  '2003-10-1'LEFT  JOIN donation_associations AS da2 ON da2.contact_id = c.contact_idLEFT  JOIN donations AS d2 ON d2.donation_id = da2.donation_id AND d2.date >=  '2003-10-1'GROUP  BY c.contact_idORDER  BY c.primary_name ASC , c.first_name ASC;