SELECT c. * , r.year, g.`group` , g.short_name, t.amount AS tuition, sum( p.amount )  AS paidFROM contacts AS cJOIN roster_memberships AS m ON m.contact_id = c.contact_idJOIN rosters AS r ON r.roster_id = m.roster_idJOIN groups AS g ON g.group_id = r.group_idJOIN roles AS l ON l.role_id = m.role_idLEFT  JOIN tuitions AS t ON t.roster_id = r.roster_idLEFT  JOIN payments AS p ON p.tuition_id = t.tuition_id AND p.contact_id = c.contact_idWHERE r.year =2004 AND ( l.role =  'Trekker' OR l.role =  'CA' OR l.role =  'QM' )GROUP  BY c.contact_idORDER  BY g.short_name ASC , c.primary_name ASC , c.first_name ASC;