SELECT d.donation_id, d.date, d.amount, f.fund, d.check_number, d.share_count, d.share_company, d.share_value, d.purpose, t.title, c.first_name, c.middle_name, c.primary_name, c.nickname, deg.degree, t2.title AS title2, c2.first_name AS first_name2, c2.middle_name AS middle_name2, c2.primary_name AS primary_name2, c2.nickname AS nickname2, deg2.degree AS degree2, a .  * FROM contacts AS cJOIN donations AS d ON d.donor_id = c.contact_idJOIN funds AS f ON f.fund_id = d.fund_idLEFT  JOIN titles AS t ON t.title_id = c.title_idLEFT  JOIN degrees AS deg ON deg.degree_id = c.degree_idJOIN address_associations AS aa ON aa.contact_id = c.contact_idLEFT  JOIN donation_associations AS da ON da.donation_id = d.donation_id AND da.contact_id <> c.contact_idLEFT  JOIN contacts AS c2 ON c2.contact_id = da.contact_idLEFT  JOIN titles AS t2 ON t2.title_id = c2.title_idLEFT  JOIN degrees AS deg2 ON deg2.degree_id = c2.degree_idJOIN addresses AS a ON a.address_id = aa.address_id/* where d.modified >= '[variable]' */;