SELECT t.title, c.first_name, c.primary_name, deg.degree, t2.title AS shared_title, c2.first_name AS shared_first_name, c2.primary_name AS shared_primary_name, deg2.degree AS shared_degree, d.date, d.amount, f.fund, a.street_address_1, a.street_address_2, a.city, a.state, a.postal_codeFROM contacts AS cJOIN donations AS d ON d.donor_id = c.contact_id AND d.date >=  '2003-10-1'JOIN funds AS f ON f.fund_id = d.fund_idJOIN address_associations AS aa ON aa.contact_id = c.contact_idJOIN addresses AS a ON a.address_id = aa.address_idLEFT  JOIN degrees AS deg ON deg.degree_id = c.degree_idLEFT  JOIN titles AS t ON t.title_id = c.title_idLEFT  JOIN donation_associations AS da ON da.donation_id = d.donation_id AND da.contact_id <> c.contact_idLEFT  JOIN contacts AS c2 ON c2.contact_id = da.contact_idLEFT  JOIN titles AS t2 ON t2.title_id = c2.title_idLEFT  JOIN degrees AS deg2 ON deg2.degree_id = c2.degree_idORDER  BY c.primary_name ASC , c.first_name ASC , f.fund ASC;