SELECT title, first_name, middle_name, primary_name, degree, nickname, SUM( amount )  AS total, fundFROM contacts AS cLEFT  JOIN titles AS t ON c.title_id = t.title_idLEFT  JOIN degrees AS deg ON c.degree_id = deg.degree_idJOIN donations AS d ON c.contact_id = d.donor_idJOIN funds AS f ON d.fund_id = f.fund_id/* WHERE date >=  '[variable]' */GROUP  BY d.donation_idORDER  BY primary_name ASC , first_name ASC;